// Import necessary classes
import org.apache.nifi.flowfile.FlowFile
import org.apache.nifi.processor.io.InputStreamCallback
import org.apache.nifi.processor.io.OutputStreamCallback
import org.apache.commons.io.IOUtils
import groovy.json.JsonSlurper
import groovy.json.JsonOutput
import java.util.UUID

// Get the current flow file
def flowFile = session.get()
if (flowFile != null) {

    // Read the content of the flow file
    def jsonContent = null
    session.read(flowFile, { inputStream ->
        jsonContent = IOUtils.toString(inputStream, 'UTF-8')
    } as InputStreamCallback)

    // Parse the JSON content
    def jsonSlurper = new JsonSlurper()
    def orderList = jsonSlurper.parseText(jsonContent)

    // Ensure the parsed content is a list (the array from JOLT)
    if (orderList instanceof List) {
        // Iterate through each object in the list and add the supplier_order_id
        orderList.each { orderObject ->
            // Generate a new UUID for each order
            orderObject.supplier_order_id = UUID.randomUUID().toString()
        }

        // Convert the modified list back to a JSON string
        def updatedJsonContent = JsonOutput.toJson(orderList)

        // Write the updated JSON content back to the flow file
        flowFile = session.write(flowFile, { outputStream ->
            outputStream.write(updatedJsonContent.getBytes('UTF-8'))
        } as OutputStreamCallback)

        // Transfer the flow file to the success relationship
        session.transfer(flowFile, REL_SUCCESS)

    } else {
        // If the content is not a list, log an error and transfer to failure
        log.error("FlowFile content was not a JSON array after JOLT transformation.")
        session.transfer(flowFile, REL_FAILURE)
    }

}
